import React from "react";
import axios from "axios";
import Header from "./components/Header";

export default class Requests extends React.Component {
  state = {
    laT1: 0,
    lnG1: 0,
    zipcode: 33166
  };

  // componentDidMount() {
  //   this.loadZipcode();
  // }


  // loadZipcode = ()=>{axios
  //   .get(
  //     "https://cors-anywhere.herokuapp.com/https://www.zipcodeapi.com/rest/c1wr56zxJrT9VdP63xSMddq1jzu3difJ6qcIpH87Ak8MWbBRQsfzPP3r5ISW15ET/info.json/" +
  //       this.state.zipcode +
  //       "/degrees"
  //   )
  //   .then(res => {
  //     this.setState({ laT1:res.lat, lnG1:res.lng})
  //     .catch(err => console.log(err));
  //   });
  // }



 // make the GET request to fetch data from the URL then using promise function to handle response.
  componentDidMount() {
    axios
      .get(
        "https://cors-anywhere.herokuapp.com/https://www.zipcodeapi.com/rest/c1wr56zxJrT9VdP63xSMddq1jzu3difJ6qcIpH87Ak8MWbBRQsfzPP3r5ISW15ET/info.json/" +
          this.state.zipcode +
          "/degrees"
      )
      .then(res => {
        this.setState({ laT1:res.lat, lnG1:res.lng})
        .catch(err => console.log(err));
      });
  }

  render() {

    return (
      <Header>
        latT1 ={this.state.latT1};
        lnG1 ={this.state.lnG1}
      </Header>

    );
  }
}
