import React, { Component } from "react";
import { Map, InfoWindow, Marker, GoogleApiWrapper } from "google-maps-react";
import API from "./utils/API";
import Wrapper from "./components/Wrapper";
import Form from "./components/Form";
import { Container } from "./components/Grid";

const mapStyles = {
  width: "50%",
  height: "50%"
};

export class MapContainer extends Component {
  state = {
    laT1: 0,
    lnG1: 0,
    zipcode: 0,
    elements: [],
    activeMarker: {},
    selectedPlace: {},
    showingInfoWindow: false
  };

  componentDidMount() {
    this.loadZipCode();
  }

  loadZipCode = () => {
    API.getZipCode()
      .then(res => this.setState({ location: res.data, lat: "", lng: "" }))
      .catch(err => console.log(err));
  };

  handleInputChange = event => {
    this.setState({ zipcode: event.target.value });
  };

  handleFormSubmit = event => {
    event.preventDefault();
    API.getZipCode(this.state.zipcode)
      .then(res => {
        if (res.data.status === "error") {
          throw new Error(res.data.location);
        }
        this.setState({ laT1: res.data.lat, lnG1: res.data.lng });
        console.log(this.state.laT1, this.state.lnG1);
        this.secondfunc(this.state.laT1, this.state.lnG1);
      })
      .catch(err => this.setState({ error: err.location }));
  };

  secondfunc = () => {
    API.getDoctor(this.state.laT1, this.state.lnG1).then(res => {
      const results = Object.keys(res.data).map(i => res.data[i]);
      var newStateArray = this.state.elements.slice();
      for (var i = 0; i < results[1].length; i++) {
        newStateArray.push({
          id: i,
          lat: results[1][i].lat,
          long: results[1][i].lon,
          name: results[1][i].name,
          street: results[1][i].visit_address.street,
          city: results[1][i].visit_address.city,
          state: results[1][i].visit_address.state,
          zip: results[1][i].visit_address.zip,
          phone: results[1][i].phones[0].number,
          card:
            '<div id="content">' +
            '<div id="siteNotice">' +
            "</div>" +
            results[1][i].name +
            "<div" +
            results[1][i].visit_address.street +
            "</div>" +
            "<div>" +
            results[1][i].visit_address.city +
            "," +
            " " +
            results[1][i].visit_address.state +
            "," +
            " " +
            results[1][i].visit_address.zip +
            "</div>" +
            "<div>" +
            "phone:" +
            results[1][i].phones[0].number +
            "</div>"
        });
      }
      this.setState({ elements: newStateArray });
      console.log(this.state.elements);
    });
  };

  onMarkerClick = (props, marker) =>
    this.setState({
      activeMarker: marker,
      selectedPlace: props,
      showingInfoWindow: true
    });

  onInfoWindowClose = () =>
    this.setState({
      activeMarker: null,
      showingInfoWindow: false
    });

  onMapClicked = () => {
    if (this.state.showingInfoWindow)
      this.setState({
        activeMarker: null,
        showingInfoWindow: false
      });
  };

  render() {
    return (
      <Wrapper>
        <Container>
          <div className="col s6">
            <Form
              handleFormSubmit={this.handleFormSubmit}
              handleInputChange={this.handleInputChange}
            />
          </div>
          <br />
          <br />
          <div className="col s6">
            <Map
              className="map"
              google={this.props.google}
              onClick={this.onMapClicked}
              zoom={14}
              initialCenter={{ lat: 25.827848, lng: -80.316078 }}
              style={mapStyles}
            >
              {this.state.elements.map(obj => (
                <Marker
                  id={obj.id}
                  name={"marker:" + obj.name}
                  position={{
                    lat: obj.lat,
                    lng: obj.lng
                  }}
                  icon={{
                    url:
                      "https://img.icons8.com/emoji/48/000000/man-health-worker.png"
                  }}
                  onClick={this.onMarkerClick}
                />
                // <InfoWindow
                //   marker={this.state.activeMarker}
                //   onClose={this.onInfoWindowClose}
                //   visible={this.state.showingInfoWindow}
                // >
                //   <div>{this.state.selectedPlace.name}</div>
                // </InfoWindow>
              ))}
            </Map>
          </div>
        </Container>
      </Wrapper>
    );
  }
}

const APIkey3 = process.env.REACT_APP_GOOGLE_KEY;

export default GoogleApiWrapper({
  apiKey: APIkey3
})(MapContainer);

